def.body:
if left >= right
  return
end
middle = ((left + right) / 2).to_i
sort(left, right, divx)
@tx[middle], @ty[middle] = @points[middle].x, @points[middle].y
@count[middle] = right - left
@minx[middle], @miny[middle] = T::MAX, T::MAX
@maxx[middle], @maxy[middle] = T::MIN, T::MIN
(left...right).each do |i|
  @minx[middle] = Math.min(@minx[middle], @points[i].x)
  @miny[middle] = Math.min(@miny[middle], @points[i].y)
  @maxx[middle] = Math.max(@maxx[middle], @points[i].x)
  @maxy[middle] = Math.max(@maxy[middle], @points[i].y)
end
build(left, middle, !divx)
build(middle + 1, right, !divx)

@current = @current + 1
u, v = root(u, @current), root(v, @current)
if u == v
  return false
end
if @parent[u].last.first > @parent[v].last.first
  u, v = v, u
end
@parent[u] << {@parent[u].last.first + @parent[v].last.first, @current}
@parent[v] << {u, @current}
true

left_root, right_root = split_rec(index, @root)
left = RandomizedBinarySearchTree(T).new(left_root)
right = RandomizedBinarySearchTree(T).new(right_root)
@root = nil
{left, right}

@graph[node_id].map do |target_id, capacity, rev|
  {target_id, capacity, rev}
end

e = @graph[v][index]
r = @graph[e[0]][e[2]]
@graph[v][index] = {e[0], e[1] - f, e[2]}
@graph[e[0]][e[2]] = {r[0], r[1] + f, r[2]}
f

cur = ROOT
str.each_char do |ch|
  c = ch.bytes.first
  if (@nodes[cur].@children)[c] == -1
    @size = @size + 1
    @nodes[@size].parent = cur
    @nodes[@size].char = ch
    (@nodes[cur].@children)[c] = @size
  end
  cur = (@nodes[cur].@children)[c]
end
@nodes[cur].is_leaf = true

if @nodes[id].link == -1
  if @nodes[id].parent == ROOT
    @nodes[id].link = ROOT
  else
    @nodes[id].link = goto(failure(@nodes[id].parent), @nodes[id].char)
  end
end
@nodes[id].link
